mapper.select:
select
  id, userid, username, tablename, role, token, addtime, expiratedtime
from token


        and id = #{id}


        and userid = #{userid}


        and username = #{username}


        and tablename = #{tablename}


        and role = #{role}


        and token = #{token}


        and addtime = #{addtime}


        and expiratedtime = #{expiratedtime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from token


        and id = #{id}


        and userid = #{userid}


        and username = #{username}


        and tablename = #{tablename}


        and role = #{role}


        and token = #{token}


        and addtime = #{addtime}


        and expiratedtime = #{expiratedtime}
