mapper.select:
select
  id, addtime, yonghuzhanghao, yonghuxingming, mima, xingbie, nianling, lianxidianhua, dianziyouxiang, shenfenzhenghao, jiatingdizhi
from yonghu


        and id = #{id}


        and addtime = #{addtime}


        and yonghuzhanghao = #{yonghuzhanghao}


        and yonghuxingming = #{yonghuxingming}


        and mima = #{mima}


        and xingbie = #{xingbie}


        and nianling = #{nianling}


        and lianxidianhua = #{lianxidianhua}


        and dianziyouxiang = #{dianziyouxiang}


        and shenfenzhenghao = #{shenfenzhenghao}


        and jiatingdizhi = #{jiatingdizhi}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from yonghu


        and id = #{id}


        and addtime = #{addtime}


        and yonghuzhanghao = #{yonghuzhanghao}


        and yonghuxingming = #{yonghuxingming}


        and mima = #{mima}


        and xingbie = #{xingbie}


        and nianling = #{nianling}


        and lianxidianhua = #{lianxidianhua}


        and dianziyouxiang = #{dianziyouxiang}


        and shenfenzhenghao = #{shenfenzhenghao}


        and jiatingdizhi = #{jiatingdizhi}
